import React, { Component } from 'react'
// import PropTypes from 'prop-types'
import { Redirect } from 'react-router-dom'
import { styler, theme } from '../../../styles'
import { Button } from '../../../components/Button'
import { path } from '../../../utils/const'

const styles = styler({
  root: {
    flex: 1,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: theme.colors.lightGrayPurple,
  },
  contents: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    width: '80%',
  },
  img: {
    width: 100,
    height: 100,
    color: theme.colors.purple,
  },
  title: {
    fontSize: 30,
    marginTop: 80,
  },
  desc: {
    whiteSpace: 'pre-line',
    fontSize: 20,
    marginTop: 40,
  },
  btn: {
    padding: '10px 20px',
    fontSize: 20,
    marginTop: 100,
    borderRadius: 10,
    '&::before': {
      borderRadius: 10,
    },
    '&:hover::before': {
      borderRadius: 10,
    },
  }
})

class ConfirmEmail extends Component {
  render() {
    const { history, location: { state: { email } } } = this.props
    if (!email) return <Redirect to={path.auth.login} />
    return (
      <div className={styles.root}>
        <div className={styles.contents}>
          <img
            src={theme.images.paperPlane}
            className={styles.img}  
          />
          <h2 className={styles.title}>確認メールを送信しました</h2>
          <p className={styles.desc}>{`確認メールを ${email}へ送信しました。送信された確認メール内に記載されたURLにアクセスし、登録の完了をお願いします。`}</p>
          <Button
            label="登録を完了してログイン"
            className={`btn-purple-gradation ${styles.btn}`}
            onClick={() => history.push(path.auth.login)}
          />
        </div>
      </div>
    )
  }
}

ConfirmEmail.propTypes = {}
ConfirmEmail.defaultProps = {}

export default ConfirmEmail
